import React from 'react';

class Weather extends React.Component {
    render() {
        return (
            <div className="weather__info">
                {
                    this.props.city && this.props.country && <p> Locaion:
                        {this.props.city}, {this.props.country}</p>
                }

                {
                    this.props.temperature && <p> Temperatur:
                        {this.props.temperature}</p>
                }

                {
                    this.props.humidity && <p> Humidity:
                        {this.props.humidity}</p>
                }
                {
                    this.props.description && <p> Conditions:
                        {this.props.description}</p>
                }
                {
                    this.props.error && <p>
                        {this.props.error}</p>
                }
            </div>
        );
    }
}
;

export default Weather;
